import React from "react";

const LoadMore = (props) => {
  let loadMessages = null;

  if (props.filtered) {
    loadMessages =
      props.filteredMessages.length - props.filteredCutoff > 0 &&
      props.filteredMessages.length > 14 ? (
        <div onClick={() => loadMoreMessages(14)}>LOAD MORE</div>
      ) : null;
  }
};

export default LoadMore;
