const Cockpit = (props) => {
  let cockpitBody = (
    <Suspense fallback={<Spinner />}>
      <Switch
      // atEnter={slideTransition.atEnter}
      // atLeave={slideTransition.atLeave}
      // atActive={slideTransition.atActive}
      // mapStyles={mapStyles}
      // className={styles.Cockpit}
      >
        <Route path="/post/:id">
          <ContentContainer>
            <FullPost {...props} />
          </ContentContainer>
        </Route>
        <Route exact path="/">
          <Landing {...props} />
        </Route>
      </Switch>
    </Suspense>
  );
  //Landing
  //Resources
  //About
  //Post

  return cockpitBody;
};

export default Cockpit;
