import React from "react";
import styles from "./PostViewer.module.css";
import Close from "../UI/Buttons/Close/Close";
import Container from "react-bootstrap/Container";

const PostModal = props => {
  let classes = [styles.PostViewer];

  if (props.open) {
    classes = [styles.PostViewer, styles.PostViewerOpen];
  }

  return (
    <div className={styles.PostContainer}>
      <div className={classes.join(" ")}>
        <Container>
          <Close openHandler={props.openHandler} />
          <p>
            <span className={styles.PostAttribute}>
              {" "}
              {props.post === null ? null : props.post.who}
            </span>
            ,
          </p>
          <p className={styles.PostMessage}>{props.post.message}</p>
          <p>
            From your{" "}
            <span className={styles.PostAttribute}>{props.post.identity}</span>
          </p>
        </Container>
      </div>
    </div>
  );
};

export default PostModal;
