import React, { useState, useEffect } from "react";
import Button from "../UI/Buttons/Button/Button";
import styles from "./Message.module.css";
import Form from "react-bootstrap/Form";

const Message = props => {
  const [messageAnswer, setMessageAnswer] = useState("");

  useEffect(() => setMessageAnswer(props.messageValue), [props.messageValue]);

  return (
    <>
      <h3>My message ...</h3>
      <div className={styles.MessageContent}>
        <Form onChange={event => setMessageAnswer(event.target.value)}>
          <Form.Group controlId="MessageContent">
            <Form.Control
              as="textarea"
              rows="10"
              className={styles.MessageContent}
              value={messageAnswer}
            />
          </Form.Group>
        </Form>
        <Button
          type="prev"
          switchHandler={props.switchHandler}
          name="message"
          formValue={messageAnswer}
        />
        <Button
          type="next"
          switchHandler={props.switchHandler}
          name="message"
          validated={
            messageAnswer !== null && messageAnswer.trim().indexOf(" ") !== -1
          }
          formValue={messageAnswer}
        />
      </div>
    </>
  );
};

export default Message;
