import React, { useState } from "react";
import styles from "./Posts.module.css";
import Post from "../../components/Post/Post";
import Modal from "../../components/UI/Modal/Modal";

const Posts = props => {
  const [entries, setEntries] = useState([
    "1) Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad mini.",
    "2) Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad mini."
  ]);
  const [open, setOpen] = useState(false);
  const [currentPost, setCurrentPost] = useState(null);
  const openHandler = () => setOpen(oldState => !oldState);
  const postSelector = post => {
    setCurrentPost(post);
    openHandler();
  };

  console.log(currentPost);

  return (
    <>
      <Modal open={open} openHandler={openHandler}>
        {currentPost}
      </Modal>
      <div className={styles.Posts}>
        {entries.map(post => (
          <Post entry={post} postSelector={postSelector} />
        ))}
      </div>
    </>
  );
};

export default Posts;
