import React, { useState } from "react";
import styles from "./FilterOptions.module.css";
import FilterOption from "./FilterOption/FilterOption";
import SortOption from "./SortOption/SortOption";
// import Close from "../UI/Buttons/Close/Close";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faClock, faPen, faUserCheck } from "@fortawesome/free-solid-svg-icons";

// import { useDispatch, useSelector } from "react-redux";
// import { addFilter, removeFilter } from "../../store/actions";
import Container from "react-bootstrap/Container";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";

const FilterOptions = props => {
  const [whoSelected, setWhoSelected] = useState(null);
  const [identitySelected, setIdentitySelected] = useState(null);
  const [sortSelected, setSortSelected] = useState(null);

  const filterHandler = (filter, name) => {
    if (whoSelected === filter && name === "who") {
      setWhoSelected(null);
    } else if (identitySelected === filter && name === "identity") {
      setIdentitySelected(null);
    } else if (name === "who") {
      setWhoSelected(filter);
    } else if (name === "identity") {
      setIdentitySelected(filter);
    }
  };

  const sortHandler = filter => {
    if (sortSelected === filter) {
      setSortSelected(false);
    } else {
      setSortSelected(filter);
    }
  };

  let parentClasses = styles.ContentViewer;
  let childClasses = styles.FilterOptions;

  if (props.expand) {
    parentClasses = [styles.ContentViewer, styles.ContentViewerOpen].join(" ");
    childClasses = [styles.FilterOptions, styles.OpenFilterOptions].join(" ");
  }

  const whoFilters = [
    "Grandmother",
    "Grandfather",
    "Mother",
    "Father",
    "Son",
    "Daughter",
    "Grandson",
    "Granddaughter",
    "Sister",
    "Brother",
    "Cousin",
    "Friend",
    "Husband",
    "Wife",
    "Coworker",
    "Classmate",
    "Neighbor",
    "Other"
  ].map(person => (
    <FilterOption
      filterHandler={filterHandler}
      filterSelected={whoSelected}
      filter={person}
      name="who"
    />
  ));

  const identityFilters = [
    "Grandmother",
    "Grandfather",
    "Mother",
    "Father",
    "Son",
    "Daughter",
    "Grandson",
    "Granddaughter",
    "Sister",
    "Brother",
    "Cousin",
    "Friend",
    "Husband",
    "Wife",
    "Coworker",
    "Classmate",
    "Neighbor",
    "Other"
  ].map(person => (
    <FilterOption
      filterHandler={filterHandler}
      filterSelected={identitySelected}
      filter={person}
      name="identity"
    />
  ));

  const sortOptions = ["Newest", "Oldest"].map(type => (
    <SortOption
      sortHandler={sortHandler}
      sortSelected={sortSelected}
      filter={type}
    />
  ));

  return (
    <div className={styles.ContentContainer}>
      <div className={parentClasses}>
        <div className={childClasses}>
          <Container>
            <span className={styles.CloseButton} onClick={props.openHandler}>
              X
            </span>
            <Row>
              <Col lg={4}>
                <span className={styles.Header}>
                  <FontAwesomeIcon
                    icon={faPen}
                    size="1x"
                    style={{ color: "orange" }}
                  />{" "}
                  Addressed to
                </span>
                <br />
                {whoFilters}
              </Col>
              <Col lg={4} className={styles.FilterOptionsMiddle}>
                <span className={styles.Header}>
                  <FontAwesomeIcon
                    icon={faUserCheck}
                    size="1x"
                    style={{ color: "orange" }}
                  />{" "}
                  Sent by
                </span>
                <br />
                {identityFilters}
              </Col>
              <Col lg={4}>
                <span className={styles.Header}>
                  <FontAwesomeIcon
                    icon={faClock}
                    size="1x"
                    style={{ color: "orange" }}
                  />{" "}
                  By Date
                </span>
                <br />
                {sortOptions}
              </Col>
            </Row>
          </Container>
        </div>
      </div>
    </div>
  );
};

export default FilterOptions;
