import * as actionTypes from "../actions/actionTypes";

const initialState = {
  posts: [],
  currentPost: null,
  filteredPosts: [],
  // filters: [],
  success: false,
  loading: false,
  error: null
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case actionTypes.SUBMIT_POST_START:
      return {
        ...state,
        loading: true
      };
    case actionTypes.SUBMIT_POST_SUCCESS:
      return {
        ...state,
        posts: [action.post, ...state.posts],
        success: true,
        loading: true
      };
    case actionTypes.SUBMIT_POST_FAIL:
      return {
        ...state,
        loading: false,
        error: action.error
      };
    case actionTypes.SUBMIT_POST_RESET:
      return {
        ...state,
        loading: false,
        error: false,
        success: false
      };
    case actionTypes.ADD_FILTER:
      return {
        ...state,
        filters: [action.filter, ...state.filters],
        loading: true
      };
    case actionTypes.REMOVE_FILTER:
      return {
        ...state,
        filters: [action.filter, ...state.filters],
        loading: true
      };
    case actionTypes.FILTER_POSTS_WHO:
      let whoFiltered = state.posts.filter(post => post === item);

      return {
        ...state,
        filteredPosts: [...whoFiltered],
        loading: false,
        error: false
      };
    case actionTypes.FILTER_POSTS_IDENTITY:
      let identityFiltered =
        state.filteredPosts.length > 0
          ? [...state.filteredPosts]
          : [...state.posts];
      state.filters.forEach(item => {
        identityFiltered = state.identityFiltered.filter(post => post === item);
      });
      return {
        ...state,
        filteredPosts: [...identityFiltered],
        loading: false,
        error: false
      };
    case actionTypes.FILTER_POSTS_DATE:
      let dateFiltered =
        state.filteredPosts.length > 0
          ? [...state.filteredPosts]
          : [...state.posts];
      state.filters.forEach(item => {
        dateFiltered = state.dateFiltered.filter(post => post === item);
      });
      return {
        ...state,
        filteredPosts: [...dateFiltered],
        loading: false,
        error: false
      };
    case actionTypes.FILTER_RESET:
      return {
        ...state,
        filteredPosts: [],
        loading: false,
        error: false,
        success: false
      };

    default:
      return state;
  }
};

export default reducer;
