import React, { useState } from "react";
import styles from "./Post.module.css";

const Post = props => {
  const summaryPost = (count, message) => {
    const newBody =
      message.indexOf(" ") === -1 ? message.split("") : message.split(" ");

    if (newBody.length < count) {
      return newBody.join(" ");
    }

    const lastEl = newBody[count - 1].split("").slice(-1)[0];
    const punctuation = [".", "!", ",", "?", ";", ":", "&"];

    if (punctuation.indexOf(lastEl) !== -1) {
      return `${newBody.slice(0, count + 1).join(" ")}...`;
    }
    return `${newBody.slice(0, count).join(" ")}...`;
  };

  return (
    <>
      <hr />
      <div
        className={styles.Post}
        onClick={() => props.postSelector(props.entry)}
      >
        {summaryPost(25, props.entry)}
      </div>
    </>
  );
};

export default Post;
