import React, { useState, useEffect } from "react";
import Button from "../UI/Buttons/Button/Button";
import styles from "./Message.module.css";
import Form from "react-bootstrap/Form";
import "medium-editor/dist/css/medium-editor.css";
import "medium-editor/dist/css/themes/default.css";
import Editor from "react-medium-editor";

// import { Editor } from "@tinymce/tinymce-react";

const Message = props => {
  const [messageAnswer, setMessageAnswer] = useState("");

  const handleChange = (text, medium) => {
    console.log(medium);

    setMessageAnswer(text);
  };

  useEffect(() => setMessageAnswer(props.messageValue), [props.messageValue]);

  return (
    <>
      <h3>My message ...</h3>
      <div>
        <Editor
          className={styles.MessageContent}
          // tag="div"
          text={messageAnswer}
          onChange={handleChange}
          options={{ toolbar: { buttons: ["bold", "italic", "underline"] } }}
        />

        {/* <Editor
          apiKey="ulzlzwd1vwer6c373nxfqmeq3tejpex1ximmhkpuw91l8dlb"
          tagName={styles.MessageContent}
          value={messageAnswer}
          init={{
            height: 500,
            menubar: false,
            plugins: [
              "advlist autolink lists link image charmap print preview anchor",
              "searchreplace visualblocks code fullscreen",
              "insertdatetime media table paste code help wordcount"
            ],
            toolbar:
              "undo redo | bold italic backcolor | \
             bullist numlist outdent indent |"
          }}
          onEditorChange={handleEditorChange}
        /> */}
        {/* <Form>
          <Form.Group controlId="MessageContent">
            <Form.Control
              onChange={event => setMessageAnswer(event.target.value)}
              as="textarea"
              rows="10"
              className={styles.MessageContent}
              value={messageAnswer}
            />
          </Form.Group>
        </Form> */}
        <Button
          type="prev"
          switchHandler={props.switchHandler}
          name="message"
          formValue={messageAnswer}
        />
        <Button
          type="next"
          switchHandler={props.switchHandler}
          name="message"
          validated={
            messageAnswer !== "" && messageAnswer.trim().indexOf(" ") !== -1
          }
          formValue={messageAnswer}
        />
      </div>
    </>
  );
};

export default Message;
