import React, { useState } from "react";
import styles from "./Posts.module.css";
import { useSelector } from "react-redux";
import Post from "../../components/Post/Post";
import PostViewer from "../../components/PostViewer/PostViewer";
import PostFilter from "../PostFilter/PostFilter";
import Spinner from "../../components/UI/Spinners/FormSpinner/FormSpinner";

const Posts = () => {
  const [open, setOpen] = useState(false);
  const [currentPost, setCurrentPost] = useState(null);
  const posts = useSelector(state => state.posts);
  const filters = useSelector(state => state.filters);
  const filteredPosts = useSelector(state => state.filteredPosts);
  const postsLoading = useSelector(state => state.loading);
  const openHandler = () => setOpen(oldState => !oldState);

  const postSelector = post => {
    setCurrentPost(post);
    openHandler();
  };

  let postFilter = posts.length > 0 ? <PostFilter /> : null;

  let postsContainer = null;

  if (filters.length > 0) {
    postsContainer = filteredPosts.map((post, index) => {
      return (
        <Post
          key={index}
          entry={post}
          open={open}
          postSelector={postSelector}
          marker={index}
        ></Post>
      );
    });
  } else {
    postsContainer = posts.map((post, index) => {
      return (
        <Post
          key={index}
          entry={post}
          open={open}
          postSelector={postSelector}
          marker={index}
        ></Post>
      );
    });
  }

  return (
    <>
      <PostViewer open={open} openHandler={openHandler} post={currentPost} />
      {postFilter}
      <div className={styles.Posts}>{postsContainer}</div>
    </>
  );
};

export default Posts;
