import * as actionTypes from "../actions/actionTypes";

const initialState = {
  posts: [],
  currentPost: null,
  filteredPosts: [],
  filters: [],
  success: false,
  loading: false,
  error: null
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case actionTypes.SUBMIT_POST_START:
      return {
        ...state,
        loading: true,
        error: null
      };
    case actionTypes.SUBMIT_POST_SUCCESS:
      return {
        ...state,
        posts: [action.post, ...state.posts],
        success: true,
        loading: true
      };
    case actionTypes.SUBMIT_POST_FAIL:
      return {
        ...state,
        loading: false,
        error: action.error
      };
    case actionTypes.SUBMIT_POST_RESET:
      return {
        ...state,
        loading: false,
        error: null,
        success: false
      };
    case actionTypes.FILTER_POSTS_START:
      return {
        ...state,
        loading: true,
        error: null
      };
    case actionTypes.FILTER_POSTS_FAIL:
      return {
        ...state,
        loading: false,
        error: action.error
      };
    case actionTypes.FILTER_POSTS_ADD:
      const filtersCopy = [...state.filters, action.filter];
      const postsCopy = [...state.posts];
      const filteredAdd = postsCopy.filter(post => {
        return post[`${action.name}`] === action.filter;
      });
      return {
        ...state,
        filteredPosts: [...state.filteredPosts, ...filteredAdd],
        loading: false
      };
    case actionTypes.FILTER_POSTS_REMOVE:
      const filteredCopy = [...state.filteredPosts];
      const filteredRemove = filteredCopy.filter(
        post => post[`${action.name}`] !== action.filter
      );
      return {
        ...state,
        filteredPosts: [...filteredRemove],
        loading: false
      };
    case actionTypes.FILTER_RESET:
      return {
        ...state,
        filteredPosts: [],
        loading: false,
        error: null
      };

    default:
      return state;
  }
};

export default reducer;
