import React, { useState, useEffect } from "react";
import Button from "../UI/Buttons/Button/Button";
import styles from "./Message.module.css";
import CKEditor from "@ckeditor/ckeditor5-react";
import BalloonEditor from "@ckeditor/ckeditor5-build-balloon";

const Message = props => {
  const [messageAnswer, setMessageAnswer] = useState("");

  const onChangeEditor = (event, editor) => {
    const data = editor.getData();
    setMessageAnswer(data);
  };

  useEffect(() => setMessageAnswer(props.messageValue), [props.messageValue]);

  return (
    <>
      <h3>My message ...</h3>
      <div className={styles.MessageContent}>
        <CKEditor
          editor={BalloonEditor}
          data={messageAnswer}
          config={{
            toolbar: ["bold", "italic", "underline", "|", "undo", "redo"]
          }}
          onChange={onChangeEditor}
        />
        <Button
          type="prev"
          switchHandler={props.switchHandler}
          name="message"
          formValue={messageAnswer}
        />
        <Button
          type="next"
          switchHandler={props.switchHandler}
          name="message"
          validated={
            messageAnswer !== "" && messageAnswer.trim().indexOf(" ") !== -1
          }
          formValue={messageAnswer}
        />
      </div>
    </>
  );
};

export default Message;
