import React, { useState, useEffect } from "react";
import { useSelector } from "react-redux";
import Button from "../UI/Buttons/FormButton/FormButton";
import styles from "./Who.module.css";
import Form from "react-bootstrap/Form";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";

const Who = (props) => {
  const [whoAnswer, setWhoAnswer] = useState({ id: null, name: null });
  const persons = useSelector((state) => state.persons.persons);

  useEffect(() => setWhoAnswer(props.whoValue), [props.whoValue]);

  console.log(whoAnswer);

  let whoFilters = null;

  if (persons) {
    whoFilters = persons.map((person) => {
      const { _id, name } = person;
      return (
        <Form.Check
          inline
          type="radio"
          value={{ id: _id, name }}
          label={person.name}
          name="formHorizontalRadios"
        />
      );
    });
  }

  return (
    <>
      <h3>Writing to my ...</h3>
      <div className={styles.WhoAnswer}>
        {whoAnswer ? whoAnswer.name : null}
      </div>
      <div className={styles.WhoContent}>
        <Form onChange={(event) => setWhoAnswer(event.target.value)}>
          <fieldset>
            <Form.Group as={Row}>
              <div className={styles.RadioContainer}>
                <Col sm={12}>{whoFilters}</Col>
              </div>
            </Form.Group>
          </fieldset>
        </Form>
        <Button
          type="prev"
          switchHandler={props.switchHandler}
          name="who"
          formValue={whoAnswer}
        />{" "}
        <Button
          type="next"
          switchHandler={props.switchHandler}
          name="who"
          formValue={whoAnswer}
          validated={whoAnswer !== null}
        />
      </div>
    </>
  );
};

export default Who;
