    //     loading: true
    //   };
    // case actionTypes.FILTER_POSTS_WHO:
    //   let whoFiltered = state.posts.filter(post => post === action.filter);
    //   return {
    //     ...state,
    //     filteredPosts: [...whoFiltered],
    //     loading: false,
    //     error: false
    //   };
    // case actionTypes.FILTER_POSTS_IDENTITY:
    //   let identityFiltered = state.posts.filter(post => post === action.filter);
    //   state.filters.forEach(item => {
    //     identityFiltered = state.identityFiltered.filter(post => post === item);
    //   });
    //   return {
    //     ...state,
    //     filteredPosts: [...identityFiltered],
    //     loading: false,
    //     error: false
    //   };
    case actionTypes.FILTER_POSTS_ADD:
      let filteredAdd = state.posts.filter(
        post => post[`${action.name}`] === action.filter
      );
      return {
        ...state,
        filteredPosts: [state.filteredPosts, ...filteredAdd],
        loading: false,
        error: false
      };
    case actionTypes.FILTER_POSTS_ADD:
      let filteredRemove = state.filteredPosts.filter(
        post => post[`${action.name}`] !== action.filter
      );
      return {
        ...state,
        filteredPosts: [state.filteredPosts, ...filteredRemove],
        loading: false,
        error: false
      };
    case actionTypes.FILTER_RESET:
      return {
        ...state,
        filteredPosts: [],
        loading: false,
        error: false,
        success: false
      };

    default:
      return state;
  }
};

export default reducer;
