import React, { useEffect } from "react";

const LoadMore = (props) => {
  const [messagesCutoff, setMessagesCutoff] = useState(
    messages.length < 14 ? messages.length : 14
  );
  const [filteredCutoff, setFilteredCutoff] = useState(
    filteredMessages.length < 14 ? filteredMessages.length : 14
  );
  let loadMoreMessages = null;
  let loadMessages = null;

  if (props.filteredLength > 0) {
    loadMoreMessages = (count) => {
      const marker =
        props.filteredLength - props.filteredCutoff >= count
          ? count
          : props.filteredLength - props.filteredCutoff;
      props.setFilteredCutoff((prevState) => prevState + marker);
    };

    loadMessages =
      props.filteredLength - props.filteredCutoff > 0 &&
      props.filteredLength > 14 ? (
        <div onClick={() => loadMoreMessages(14)}>LOAD MORE</div>
      ) : null;
  } else {
    loadMoreMessages = (count) => {
      const marker =
        props.messagesLength - props.messagesCutoff >= count
          ? count
          : props.messagesLength - props.messagesCutoff;
      props.setMessagesCutoff((prevState) => prevState + marker);
    };

    loadMessages =
      props.messagesLength - props.messagesCutoff > 0 &&
      props.messagesLength > 14 ? (
        <div onClick={() => loadMoreMessages(14)}>LOAD MORE</div>
      ) : null;
  }

  return <>{loadMessages}</>;
};

export default LoadMore;
