import React, { useState, useReducer, useEffect } from "react";
import styles from "./PostForm.module.css";
import Modal from "../../components/UI/Modal/Modal";
import Identity from "../../components/Identity/Identity";
import Who from "../../components/Who/Who";
import Message from "../../components/Message/Message";
import Summary from "../../components/Summary/Summary";
import FormSpinner from "../../components/UI/Spinners/FormSpinner/FormSpinner";
import { useDispatch, useSelector } from "react-redux";
import { submitPost, submitPostReset } from "../../store/actions";

const initialState = {
  identity: null,
  who: null,
  message: ""
};

const formReducer = (formState, action) => {
  switch (action.type) {
    case "identity":
      return {
        ...formState,
        identity: action.value
      };
    case "who":
      return {
        ...formState,
        who: action.value
      };
    case "message":
      return {
        ...formState,
        message: action.value
      };
    case "reset":
      return {
        ...initialState
      };
    case "submit":
      return {
        ...formState
      };
    default:
      break;
  }
};

const PostForm = () => {
  const [formState, dispatchForm] = useReducer(formReducer, initialState);
  const [open, setOpen] = useState(false);
  const [sectionIndex, setSectionIndex] = useState(0);
  const postSuccess = useSelector(state => state.success);
  const postError = useSelector(state => state.error);
  const postLoading = useSelector(state => state.loading);
  const dispatch = useDispatch();

  useEffect(() => {
    if (postSuccess) {
      dispatchForm({ type: "reset" });
      dispatch(submitPostReset());
    }
  }, [postSuccess, dispatch]);

  const openHandler = () => setOpen(oldState => !oldState);

  const switchHandler = (type, name, formValue) => {
    if (type === "next" && sectionIndex === sections.length - 1) {
      setSectionIndex(prevIndex => prevIndex);
      dispatchForm({ type: name, value: formValue });
    } else if (type === "next") {
      setSectionIndex(prevIndex => prevIndex + 1);
      dispatchForm({ type: name, value: formValue });
    } else if (type === "prev") {
      setSectionIndex(prevIndex => prevIndex - 1);
      dispatchForm({ type: name, value: formValue });
    } else {
      dispatchForm({ type: name });
      const id = Array(15)
        .fill(null)
        .map(() =>
          Math.random()
            .toString(36)
            .substr(2)
        )
        .join("");
      const date = new Date();
      const body = {
        id,
        date,
        identity: formState.identity,
        who: formState.who,
        message: formState.message
      };
      dispatch(submitPost(body));
      setSectionIndex(0);
    }
  };

  const sections = [
    <Identity
      switchHandler={switchHandler}
      identityValue={formState.identity}
    />,
    <Who switchHandler={switchHandler} whoValue={formState.who} />,
    <Message switchHandler={switchHandler} messageValue={formState.message} />,
    <Summary
      switchHandler={switchHandler}
      messageValue={formState.message}
      whoValue={formState.who}
      identityValue={formState.identity}
    />
  ];

  let postFormBody = sections[sectionIndex];

  if (postLoading) {
    postFormBody = <FormSpinner />;
  } else if (postError) {
    postFormBody = postError;
  }

  return (
    <>
      <Modal open={open} openHandler={openHandler}>
        {postFormBody}
      </Modal>
      <div className={styles.TextBox} onClick={() => openHandler()}>
        Enter Here <span className={styles.TypeWriter}>|</span>
      </div>
    </>
  );
};

export default PostForm;
