import React, { useState, useEffect } from "react";
import styles from "./Messages.module.css";
import { useSelector } from "react-redux";
import MessageSnapshot from "../../components/MessageSnapshot/MessageSnapshot";
import MessageFilter from "../MessageFilter/MessageFilter";
import Spinner from "../../components/UI/Spinners/FormSpinner/FormSpinner";

const Messages = () => {
  const messages = useSelector((state) =>
    state.messages.messages.slice(0, cutoff)
  );
  const [cutoff, setCutoff] = useState(
    messages.length < 14 ? messages.length : 14
  );
  const { who, identity } = useSelector((state) => state.messages.filters);
  const sort = useSelector((state) => state.messages.sort);
  const filteredMessages = useSelector((state) =>
    state.messages.filteredMessages.slice(0, cutoff)
  );
  const messagesLoading = useSelector((state) => state.messages.loading);

  console.log(cutoff);

  // useEffect(() => (messages.length < 14 ? setCutoff(messages.length) : 14), [
  //   messages,
  // ]);

  const loadMore = (count) => {
    const marker =
      messages.length - cutoff >= count ? count : messages.length - cutoff;
    setCutoff((prevState) => prevState + marker);
  };

  let messageFilter = messages.length > 0 ? <MessageFilter /> : null;

  let messagesContainer = messagesLoading ? <Spinner /> : null;

  if (who || identity || sort) {
    messagesContainer = filteredMessages.map((message, index) => {
      return (
        <MessageSnapshot
          key={index}
          entry={message}
          marker={index}
        ></MessageSnapshot>
      );
    });
    // .reverse();
  } else {
    messagesContainer = messages.slice(0, cutoff).map((message, index) => {
      return (
        <MessageSnapshot
          key={index}
          entry={message}
          marker={index}
        ></MessageSnapshot>
      );
    });
    // .reverse();
  }

  return (
    <>
      {messageFilter}
      <div className={styles.Messages}>{messagesContainer}</div>
      {messages.length > 14 ? (
        <div onClick={() => loadMore(14)}>LOAD MORE</div>
      ) : null}
    </>
  );
};

export default Messages;

