import React from "react";
import styles from "./PostViewer.module.css";
import Close from "../UI/Buttons/Close/Close";
import parse from "html-react-parser";
import Container from "react-bootstrap/Container";

const PostViewer = props => {
  let classes = [styles.PostViewer];

  if (props.open) {
    classes = [styles.PostViewer, styles.PostViewerOpen];
  }

  return (
    <div className={styles.PostContainer}>
      <div className={classes.join(" ")}>
        <Container>
          <Close openHandler={props.openHandler} />
          <p>
            <b>
              To my{" "}
              <span className={styles.PostAttribute}>
                {props.post === null ? null : props.post.who}
              </span>
              ,
            </b>
          </p>
          <span className={styles.PostMessage}>
            {props.post === null ? null : parse(props.post.message)}
          </span>
          <p>
            <b>
              From your{" "}
              <span className={styles.PostAttribute}>
                {props.post === null ? null : props.post.identity}
              </span>
            </b>
          </p>
        </Container>
      </div>
    </div>
  );
};

export default PostViewer;
