import React, { useState } from "react";
import parse from "html-react-parser";
import styles from "./Post.module.css";

const Post = props => {
  const [expand, setExpand] = useState(false);

  const selectorHandler = () => {
    props.postSelector(props.entry);
    setExpand(prevState => !prevState);
  };

  const summaryPost = (message, count) => {
    const newBody = message.split(" ");

    if ((expand && window.innerWidth > 599) || newBody.length <= count)
      return message;

    const lastEl = newBody[count - 1].split("").slice(-1)[0];
    const punctuation = [".", "!", ",", "?", ";", ":", "&"];

    if (punctuation.indexOf(lastEl) !== -1)
      return `${newBody.slice(0, count + 1).join(" ")}...`;

    return `${newBody.slice(0, count).join(" ")}...`;
  };

  return (
    <>
      <hr />
      <div className={styles.Post} onClick={() => selectorHandler()}>
        <p>
          <b>
            To my{" "}
            <span className={styles.PostAttribute}> {props.entry.who}</span>,
          </b>
        </p>
        <span className={styles.PostMessage}>
          {parse(summaryPost(props.entry.message, 100))}
        </span>
        <p>
          <b>
            From your{" "}
            <span className={styles.PostAttribute}>{props.entry.identity}</span>
          </b>
        </p>
      </div>
    </>
  );
};

export default Post;
