import * as actionTypes from "./actionTypes";

export const addFilter = (filter, name) => {
  return dispatch => {
    try {
      dispatch(filterPostsStart());
      dispatch(filterPostsAdd(filter, name));
    } catch (err) {
      dispatch(filterPostsFail(err));
    }
  };
};

export const removeFilter = (filter, name) => {
  try {
    dispatch(filterPostsStart());
    dispatch(filterPostsRemove(filter, name));
  } catch (err) {
    dispatch(filterPostsFail(err));
  }
};

export const filterPostsAdd = (filter, name) => {
  return {
    type: actionTypes.FILTER_POSTS_ADD,
    filter,
    name
  };
};
export const filterPostsRemove = (filter, name) => {
  return {
    type: actionTypes.FILTER_POSTS_REMOVE,
    filter,
    name
  };
};

export const filterPostsStart = () => {
  return {
    type: actionTypes.FILTER_POSTS_START
  };
};

export const filterPostsFail = error => {
  return {
    type: actionTypes.FILTER_POSTS_FAIL,
    error
  };
};

export const filterReset = () => {
  return {
    type: actionTypes.FILTER_RESET
  };
};
