import React, { useState } from "react";
import styles from "./FilterOptions.module.css";
import FilterOption from "./FilterOption/FilterOption";
// import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
// import { faFilter } from "@fortawesome/free-solid-svg-icons";
import Form from "react-bootstrap/Form";
import Container from "react-bootstrap/Container";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";
import { addFilter, removeFilter } from "../../store/actions";
import { useDispatch } from "react-redux";

const FilterOptions = props => {
  const dispatch = useDispatch();

  const selectHandler = (selected, name) => {
    if (selected) {
      dispatch(addFilter(name));
    }
    dispatch(removeFilter(name));
  };

  let classes = [styles.FilterOptions];
  // let filterOptionsContainer = null;

  if (props.expand) {
    classes = [styles.FilterOptions, styles.OpenFilterOptions].join(" ");
  }

  const checkBoxes = [
    "Grandmother",
    "Grandfather",
    "Mother",
    "Father",
    "Son",
    "Daughter",
    "Grandson",
    "Granddaughter",
    "Sister",
    "Brother",
    "Cousin",
    "Friend",
    "Husband",
    "Wife",
    "Coworker",
    "Classmate",
    "Neighbor",
    "Other"
  ].map(person => (
    <FilterOption selectedHander={selectedHandler} name={person} />
  ));

  return (
    <div className={styles.FilterOption}>
      <div className={classes}>
        <Container>
          <Row>
            <Col lg={4}>
              <Form>{checkBoxes}</Form>
            </Col>
            <Col lg={4}>2</Col>
            <Col lg={4}>3</Col>
          </Row>
        </Container>
      </div>
    </div>
  );
};

export default FilterOptions;
