import React, { useState } from "react";
import styles from "./PostFilter.module.css";
import FilterOptions from "../../components/FilterOptions/FilterOptions";
// import FilterViewer from "../ContentContainer/ContentContainer";
import Close from "../../components/UI/Buttons/Close/Close";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faFilter } from "@fortawesome/free-solid-svg-icons";

const PostFilter = () => {
  const [expand, setExpand] = useState(false);
  const [selected, setSelected] = useState(false);
  // const postLoading = useSelector(state => state.loading);

  const filterHandler = () => {
    expandHandler();
    selectHanlder();
  };

  const expandHandler = () => setExpand(prevState => !prevState);
  const selectHanlder = () => setSelected(prevState => !prevState);

  const filterOptions = (
    // window.innerWidth < 600 ? (
    // <FilterViewer open={expand} openHandler={filterHandler}>
    //   <FilterOptions expand={expand} openHandler={filterHandler} />
    //   <center>
    //     <Close openHandler={filterHandler}>OK</Close>
    //   </center>
    // </FilterViewer>
    // ) : (
    <FilterOptions openHandler={filterHandler} expand={expand} />
  );
  // );

  return (
    <>
      <div onClick={() => filterHandler()}>
        <span className={selected ? styles.ButtonSelected : styles.Button}>
          {" "}
          <FontAwesomeIcon
            icon={faFilter}
            size="lg"
            style={{ color: "orange" }}
          />{" "}
          Filter
        </span>
      </div>
      {filterOptions}
    </>
  );
};

export default PostFilter;
