import React, { useState, useEffect } from "react";
import Button from "../UI/Buttons/FormButton/FormButton";
import styles from "./Identity.module.css";
import Form from "react-bootstrap/Form";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";

const Identity = props => {
  const [identityAnswer, setIdentityAnswer] = useState(null);

  useEffect(() => setIdentityAnswer(props.identityValue), [
    props.identityValue
  ]);

  const radioBoxes = [
    "Grandmother",
    "Grandfather",
    "Mother",
    "Father",
    "Son",
    "Daughter",
    "Grandson",
    "Granddaughter",
    "Sister",
    "Brother",
    "Cousin",
    "Friend",
    "Husband",
    "Wife",
    "Coworker",
    "Classmate",
    "Neighbor",
    "Other"
  ].map(person => (
    <Form.Check
      inline
      type="radio"
      value={person}
      label={person}
      name="formHorizontalRadios"
    />
  ));

  return (
    <>
      <h3>I am a ...</h3>
      <div className={styles.IdentityAnswer}>{identityAnswer}</div>
      <div className={styles.IdentityContent}>
        <Form onChange={event => setIdentityAnswer(event.target.value)}>
          <fieldset>
            <Form.Group as={Row}>
              <div className={styles.RadioContainer}>
                <Col sm={12}>{radioBoxes}</Col>
              </div>
            </Form.Group>
          </fieldset>
        </Form>
        <Button
          type="next"
          switchHandler={props.switchHandler}
          name="identity"
          validated={identityAnswer !== null}
          formValue={identityAnswer}
        />
      </div>
    </>
  );
};

export default Identity;
