import React from "react";
import styles from "./Button.module.css";

const Button = props => {
  let buttonText = null;
  switch (props.type) {
    case "next":
      buttonText = "Next";
      break;
    case "prev":
      buttonText = "Previous";
      break;
    case "submit":
      buttonText = "Submit";
      break;
    default:
      break;
  }

  let buttonBody = null;

  if (props.type === "next" || props.type === "") {
    buttonBody = (
      <span
        className={
          props.formValue === null && props.type !== "prev"
            ? styles.ButtonInactive
            : styles.Button
        }
        onClick={() => {
          if (props.formValue === null && props.type !== "prev") {
            return;
          }
          props.switchHandler(props.type, props.name, props.formValue);
        }}
      >
        {buttonText}
      </span>
    );
  } else {
    <span
      className={
        props.formValue === null ? styles.ButtonInactive : styles.Button
      }
    >
      {buttonText}
    </span>;
  }

  return { buttonBody };
};

export default Button;
