import React from "react";
import { useSelector } from "react-redux";

// export let interceptor;

const withErrorHandler = (WrappedComponent) => {
  return (props) => {
    // const [error, setError] = useState(false);
    // const [errorMessage, setErrorMessage] = useState("");
    const messagesError = useSelector((state) => state.messages.error) !== null;
    const messageError = useSelector((state) => state.message.error) !== null;
    const personsError = useSelector((state) => state.persons.error) !== null;
    // useSelector((state) => state.message.error) !== null ||
    // useSelector((state) => state.persons.error) !== null;

    // interceptor = (response) => {
    //   setError(true);
    //   setErrorMessage(`${response}`);
    // };

    let body = <WrappedComponent {...props} />;

    if (messagesError || messageError || personsError) {
      body = (
        <>
          Whoops! An error occured. IT Has been notified. Please try again
          later.
        </>
      );
    }

    return body;
  };
};

export default withErrorHandler;
