import React from "react";
import styles from "./Button.module.css";

const Button = props => {
  let buttonBody = null;
  let buttonText = null;
  switch (props.type) {
    case "next":
      buttonText = "Next";
      break;
    case "prev":
      buttonText = "Previous";
      break;
    case "submit":
      buttonText = "Submit";
      break;
    default:
      break;
  }

  return <>{buttonBody}</>;
};

export default Button;
