import React from "react";
import styles from "./PostMobile.module.css";
import Close from "../UI/Buttons/Close/Close";
import Container from "react-bootstrap/Container";

const PostMobile = props => {
  let classes = [styles.ContentViewer];

  if (props.open) {
    classes = [styles.ContentViewer, styles.ContentViewerOpen];
  }

  return (
    <div className={styles.ContentContainer}>
      <div className={classes.join(" ")}>
        <Container>
          <Close openHandler={props.openHandler} />
          {props.children}
        </Container>
      </div>
    </div>
  );
};

export default PostMobile;
