import React, { useState, useEffect } from "react";
import styles from "./Messages.module.css";
import { useSelector } from "react-redux";
import LoadMore from "../../components/LoadMore/LoadMore";
import MessageSnapshot from "../../components/MessageSnapshot/MessageSnapshot";
import MessageFilter from "../MessageFilter/MessageFilter";
import Spinner from "../../components/UI/Spinners/FormSpinner/FormSpinner";

const Messages = () => {
  const messages = useSelector((state) => state.messages.messages);
  const { who, identity } = useSelector((state) => state.messages.filters);
  const sort = useSelector((state) => state.messages.sort);
  const filteredMessages = useSelector(
    (state) => state.messages.filteredMessages
  );
  const [messagesCutoff, setMessagesCutoff] = useState(
    messages.length < 14 ? messages.length : 14
  );
  const [filteredCutoff, setFilteredCutoff] = useState(
    filteredMessages.length < 14 ? filteredMessages.length : 14
  );

  const messagesLoading = useSelector((state) => state.messages.loading);

  let messageFilter = messages.length > 0 ? <MessageFilter /> : null;

  let messagesContainer = messagesLoading ? <Spinner /> : null;

  if (who || identity || sort) {
    messagesContainer = filteredMessages
      .slice(0, filteredCutoff)
      .map((message, index) => {
        return (
          <MessageSnapshot
            key={index}
            entry={message}
            marker={index}
          ></MessageSnapshot>
        );
      });
  } else {
    messagesContainer = messages
      .slice(0, messagesCutoff)
      .map((message, index) => {
        return (
          <MessageSnapshot
            key={index}
            entry={message}
            marker={index}
          ></MessageSnapshot>
        );
      });
  }

  return (
    <>
      {messageFilter}
      {messagesContainer}
      <LoadMore
        messages={messages}
        filteredMessages={filteredMessages}
        setFilteredCutoff={setFilteredCutoff}
        setMessagesCutoff={setMessagesCutoff}
        messagesCutoff={messagesCutoff}
        filteredCutoff={filteredCutoff}
      />
    </>
  );
};

export default Messages;

