import React, { useState, useEffect } from "react";
import { useSelector } from "react-redux";
import Button from "../UI/Buttons/FormButton/FormButton";
import styles from "./Who.module.css";
import Form from "react-bootstrap/Form";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";

const Who = (props) => {
  const [whoAnswer, setWhoAnswer] = useState({ _id: null, name: null });
  const persons = useSelector((state) => state.persons.persons);

  useEffect(() => setWhoAnswer(props.whoValue), [props.whoValue]);
  console.log(whoAnswer);

  const whoFilters = persons.map((person) => (
    <Form.Check
      inline
      type="radio"
      value={person._id}
      label={person.name}
      name="formHorizontalRadios"
      onChange={() => setWhoAnswer(person)}
    />
  ));

  return (
    <>
      <h3>Writing to my ...</h3>
      <div className={styles.WhoAnswer}>{whoAnswer.name}</div>
      <div className={styles.WhoContent}>
        <Form>
          <fieldset>
            <Form.Group as={Row}>
              <div className={styles.RadioContainer}>
                <Col sm={12}>{whoFilters}</Col>
              </div>
            </Form.Group>
          </fieldset>
        </Form>
        <Button
          type="prev"
          switchHandler={props.switchHandler}
          name="who"
          formValue={whoAnswer}
        />{" "}
        <Button
          type="next"
          switchHandler={props.switchHandler}
          name="who"
          formValue={whoAnswer}
          validated={whoAnswer !== null}
        />
      </div>
    </>
  );
};

export default Who;
