import * as actionTypes from "./actionTypes";

export const fetchMessages = (skip, count) => {
  return async (dispatch) => {
    try {
      dispatch(fetchMessagesStart());
      const response = await fetch(
        "/api/messages?skip=${skip}?count=${count}",
        {
          method: "GET",
          mode: "cors",
          cache: "no-cache",
          credentials: "same-origin",
          headers: {
            "Content-Type": "application/json",
          },
        }
      );
      const data = await response.json();

      dispatch(fetchMessagesSuccess(data));
    } catch (err) {
      dispatch(fetchMessagesFail(err));
    }
  };
};

export const fetchMessagesStart = () => {
  return {
    type: actionTypes.FETCH_MESSAGES_START,
  };
};

export const fetchMessagesSuccess = (messages) => {
  return {
    type: actionTypes.FETCH_MESSAGES_SUCCESS,
    messages,
  };
};

export const fetchMessagesFail = (messages) => {
  return {
    type: actionTypes.FETCH_MESSAGES_SUCCESS,
    messages,
  };
};
