import React, { useState, useEffect } from "react";
import Button from "../UI/Buttons/FormButton/FormButton";
import styles from "./Who.module.css";
import Form from "react-bootstrap/Form";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";

const Who = props => {
  const [whoAnswer, setWhoAnswer] = useState(null);

  useEffect(() => setWhoAnswer(props.whoValue), [props.whoValue]);

  const whoFilters = [
    "Grandmother",
    "Grandfather",
    "Mother",
    "Father",
    "Son",
    "Daughter",
    "Grandson",
    "Granddaughter",
    "Sister",
    "Brother",
    "Cousin",
    "Friend",
    "Husband",
    "Wife",
    "Coworker",
    "Classmate",
    "Neighbor",
    "Other"
  ].map(person => (
    <Form.Check
      inline
      type="radio"
      value={person}
      label={person}
      name="formHorizontalRadios"
    />
  ));

  return (
    <>
      <h3>Writing to my ...</h3>
      <div className={styles.WhoAnswer}>{whoAnswer}</div>
      <div className={styles.WhoContent}>
        <Form onChange={event => setWhoAnswer(event.target.value)}>
          <fieldset>
            <Form.Group as={Row}>
              <div className={styles.RadioContainer}>
                <Col sm={12}>{whoFilters}</Col>
              </div>
            </Form.Group>
          </fieldset>
        </Form>
        <Button
          type="prev"
          switchHandler={props.switchHandler}
          name="who"
          formValue={whoAnswer}
        />{" "}
        <Button
          type="next"
          switchHandler={props.switchHandler}
          name="who"
          formValue={whoAnswer}
          validated={whoAnswer !== null}
        />
      </div>
    </>
  );
};

export default Who;
