import React from "react";
import styles from "./Button.module.css";

const Button = props => {
  let buttonText = null;
  switch (props.type) {
    case "next":
      buttonText = "Next";
      break;
    case "prev":
      buttonText = "Previous";
      break;

    default:
      break;
  }

  return (
    <span
      className={
        props.formValue === null ? styles.ButtonInactive : styles.Button
      }
      onClick={() => {
        props.switchHandler(props.type, props.name, props.formValue);
      }}
    >
      {buttonText}
    </span>
  );
};

export default Button;
