import React, { useState, useCallback } from "react";
import styles from "./FilterOptions.module.css";
import FilterOption from "./FilterOption/FilterOption";
import { useDispatch, useSelector } from "react-redux";
import { addFilter, removeFilter } from "../../../store/actions";
import Container from "react-bootstrap/Container";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";

const FilterOptions = props => {
  const [whoSelected, setWhoSelected] = useState(false);
  const [identitySelected, setIdentitySelected] = useState(false);
  const filters = useSelector(state => state.filters);
  const dispatch = useDispatch();

  const filterHandler = useCallback((selected, filter, name) => {
    if (!selected && filters.length > 0) {
      dispatch(removeFilter(filter, name));
    } else if (selected) {
      dispatch(addFilter(filter, name));
    }
  }, []);

  let classes = [styles.FilterOptions];

  if (props.expand) {
    classes = [styles.FilterOptions, styles.OpenFilterOptions].join(" ");
  }

  const whoFilters = [
    "Grandmother",
    "Grandfather",
    "Mother",
    "Father",
    "Son",
    "Daughter",
    "Grandson",
    "Granddaughter",
    "Sister",
    "Brother",
    "Cousin",
    "Friend",
    "Husband",
    "Wife",
    "Coworker",
    "Classmate",
    "Neighbor",
    "Other"
  ].map(person => (
    <FilterOption filterHandler={filterHandler} filter={person} name="who" />
  ));

  const identityFilters = [
    "Grandmother",
    "Grandfather",
    "Mother",
    "Father",
    "Son",
    "Daughter",
    "Grandson",
    "Granddaughter",
    "Sister",
    "Brother",
    "Cousin",
    "Friend",
    "Husband",
    "Wife",
    "Coworker",
    "Classmate",
    "Neighbor",
    "Other"
  ].map(person => <FilterOption filter={person} name="identity" />);

  return (
    <div className={styles.FilterOption}>
      <div className={classes}>
        <Container>
          <Row>
            <Col lg={4}>{whoFilters}</Col>
            <Col lg={4}>{identityFilters}</Col>
            <Col lg={4}>3</Col>
          </Row>
        </Container>
      </div>
    </div>
  );
};

export default FilterOptions;
