import React from "react";
import styles from "./PostModal.module.css";
import Backdrop from "../Backdrop/Backdrop";
import Close from "../../UI/Buttons/Close/Close";
import Container from "react-bootstrap/Container";

const PostModal = props => {
  let classes = [styles.PostModal];

  if (props.open) {
    classes = [styles.PostModal, styles.PostModalOpen];
  }

  return (
    <div className={styles.ModalContainer}>
      <Backdrop show={props.open} toggleBackdrop={props.openHandler} />
      <div className={classes.join(" ")}>
        <Close openHandler={props.openHandler} />
        {props.children}
      </div>
    </div>
  );
};

export default PostModal;
