import * as actionTypes from "../actions/actionTypes";

const initialState = {
  posts: [],
  currentPost: null,
  filteredPosts: [],
  filters: [],
  success: false,
  loading: false,
  error: null
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case actionTypes.SUBMIT_POST_START:
      return {
        ...state,
        loading: true,
        error: null
      };
    case actionTypes.SUBMIT_POST_SUCCESS:
      return {
        ...state,
        posts: [action.post, ...state.posts],
        success: true,
        loading: true
      };
    case actionTypes.SUBMIT_POST_FAIL:
      return {
        ...state,
        loading: false,
        error: action.error
      };
    case actionTypes.SUBMIT_POST_RESET:
      return {
        ...state,
        loading: false,
        error: null,
        success: false
      };
    case actionTypes.FILTER_POSTS_START:
      return {
        ...state,
        loading: true,
        error: null
      };
    case actionTypes.FILTER_POSTS_FAIL:
      return {
        ...state,
        loading: false,
        error: action.error
      };
    case actionTypes.FILTER_POSTS_ADD:
      const newFilter = { [action.name]: action.filter };
      const postsCopy = [...state.posts];
      const dupCopy = [...state.filteredPosts];
      const filteredAdd = postsCopy.filter(post => {
        const dupIndex = dupCopy.findIndex(dup => dup.id === post.id);
        return post[`${action.name}`] === action.filter && dupIndex === -1;
      });
      return {
        ...state,
        filters: [...state.filters, newFilter],
        filteredPosts: [...state.filteredPosts, ...filteredAdd],
        loading: false
      };
    case actionTypes.FILTER_POSTS_REMOVE:
      const filtersCopy = [...state.filters];
      const newFilters = filtersCopy.filter(
        item => item[`${action.name}`] !== action.filter
      );
      const filteredCopy = [...state.filteredPosts];

      const filteredRemove = filteredCopy.filter(post => {
        const whoIndex = newFilters.findIndex(
          filter => filter.who === post.who
        );
        const identityIndex = newFilters.findIndex(
          filter => filter.identity === post.identity
        );

        const indexEval = whoIndex !== -1 || identityIndex !== -1;

        console.log(indexEval);

        // return post[`${action.name}`] !== action.filter && !indexEval;
        return whoIndex !== -1 && identityIndex !== -1;
      });

      return {
        ...state,
        filters: [...newFilters],
        filteredPosts: [...filteredRemove],
        loading: false
      };
    case actionTypes.FILTER_RESET:
      return {
        ...state,
        filteredPosts: [],
        loading: false,
        error: null
      };

    default:
      return state;
  }
};

export default reducer;
