import * as actionTypes from "../actions/actionTypes";

const initialState = {
  posts: [],
  filteredPosts: [],
  currentPost: null,
  success: false,
  loading: false,
  error: null
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case actionTypes.SUBMIT_POST_START:
      return {
        ...state,
        loading: true
      };
    case actionTypes.SUBMIT_POST_SUCCESS:
      return {
        ...state,
        posts: [action.post, ...state.posts],
        success: true,
        loading: true
      };
    case actionTypes.SUBMIT_POST_FAIL:
      return {
        ...state,
        loading: false,
        error: action.error
      };
    case actionTypes.SUBMIT_POST_RESET:
      return {
        ...state,
        loading: false,
        error: false,
        success: false
      };
    case actionTypes.ADD_FILTER_START:
      return {
        ...state,
        loading: true
      };
    case actionTypes.ADD_FILTER_SUCCESS:
      return {
        ...state,
        success: true,
        loading: true
      };
    case actionTypes.ADD_FILTER_FAIL:
      return {
        ...state,
        loading: false,
        error: action.error
      };
    case actionTypes.FILTER_RESET:
      return {
        ...state,
        filteredPosts: [],
        loading: false,
        error: false,
        success: false
      };

    default:
      return state;
  }
};

export default reducer;
