import * as actionTypes from "../actions/actionTypes";

const initialState = {
  currentMessage: null,
  messageSuccess: null,
  setSuccess: null,
  loading: false,
  error: null,
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case actionTypes.FETCH_MESSAGE_START:
      return {
        ...state,
        loading: true,
        setSuccess: null,
      };
    case actionTypes.FETCH_MESSAGE_SUCCESS:
      return {
        ...state,
        currentMessage: action.message,
        setSuccess: true,
        loading: false,
      };
    case actionTypes.FETCH_MESSAGE_FAIL:
      return {
        ...state,
        loading: false,
        setSuccess: false,
      };
    case actionTypes.RESET_MESSAGE:
      return {
        ...state,
        currentMessage: null,
        setSuccess: null,
        loading: false,
      };

    default:
      return state;
  }
};

export default reducer;
