import * as actionTypes from "../actions/actionTypes";

const initialState = {
  posts: [],
  currentPost: null,
  filteredPosts: [],
  filters: { who: null, identity: null },
  sort: null,
  success: false,
  loading: false,
  error: null
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case actionTypes.SUBMIT_POST_START:
      return {
        ...state,
        loading: true,
        error: null
      };
    case actionTypes.SUBMIT_POST_SUCCESS:
      return {
        ...state,
        posts: [action.post, ...state.posts],
        success: true,
        loading: true
      };
    case actionTypes.SUBMIT_POST_FAIL:
      return {
        ...state,
        loading: false,
        error: action.error
      };
    case actionTypes.SUBMIT_POST_RESET:
      return {
        ...state,
        loading: false,
        error: null,
        success: false
      };
    case actionTypes.FIND_POST:
      const post = state.posts.find(post => action.post.id === post.id);

    //filter & sort logic below
    case actionTypes.FILTER_POSTS_START:
      return {
        ...state,
        loading: true,
        error: null
      };
    case actionTypes.FILTER_POSTS_FAIL:
      return {
        ...state,
        loading: false,
        error: action.error
      };
    case actionTypes.FILTER_POSTS_ADD:
      return {
        ...state,
        filters: {
          ...state.filters,
          [action.name]: action.filter
        }
      };
    case actionTypes.FILTER_POSTS_REMOVE:
      return {
        ...state,
        filters: {
          ...state.filters,
          [action.name]: null
        }
      };
    case actionTypes.FILTER_POSTS:
      const { who, identity } = state.filters;
      const postsCopy = [...state.posts];
      const newFilteredPosts =
        who && identity
          ? postsCopy.filter(
              post => post.who === who && post.identity === identity
            )
          : postsCopy.filter(
              post => post.who === who || post.identity === identity
            );
      return {
        ...state,
        filteredPosts: [...newFilteredPosts],
        loading: false,
        error: null
      };
    case actionTypes.FILTER_RESET:
      return {
        ...state,
        filteredPosts: [],
        loading: false,
        error: null
      };
    case actionTypes.SORT_POSTS_START:
      return {
        ...state,
        loading: true,
        error: null
      };
    case actionTypes.SORT_POSTS_FAIL:
      return {
        ...state,
        loading: false,
        error: action.error
      };
    case actionTypes.SORT_POSTS_ADD:
      return {
        ...state,
        sort: action.filter
      };
    case actionTypes.SORT_POSTS_REMOVE:
      return {
        ...state,
        sort: false
      };
    case actionTypes.SORT_POSTS:
      const filteredCopy = action.filtered
        ? [...state.filteredPosts]
        : [...state.posts];
      const sortedPosts =
        state.sort === "Oldest"
          ? filteredCopy.sort((a, b) => new Date(a.date) - new Date(b.date))
          : filteredCopy.sort((a, b) => new Date(b.date) - new Date(a.date));
      return {
        ...state,
        filteredPosts: [...sortedPosts],
        loading: false,
        error: null
      };
    case actionTypes.SORT_RESET:
      return {
        ...state,
        sort: null,
        loading: false,
        error: null
      };
    default:
      return state;
  }
};

export default reducer;
