import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import parse from "html-react-parser";
import styles from "./FullPost.module.css";
import { setPost, resetPost } from "../../store/actions";
import { useParams, withRouter } from "react-router-dom";
import Spinner from "../UI/Spinners/FormSpinner/FormSpinner";

const FullPost = props => {
  const post = useSelector(state => state.currentPost);
  const setSuccess = useSelector(state => state.setSuccess);
  const dispatch = useDispatch();
  const { id } = useParams();

  useEffect(() => {
    if (setSuccess === false) {
      dispatch(resetPost());
      props.history.push("/");
    }

    dispatch(setPost(id));

    return () => dispatch(resetPost());
  }, [dispatch, props.history, id, setSuccess]);

  let postBody = <Spinner />;

  if (post) {
    postBody = (
      <>
        <p>
          To my{" "}
          <span className={styles.PostAttribute}>
            <b>{parse(post.who)}</b>
          </span>
          ,
        </p>
        <span className={styles.PostMessage}>{parse(post.message)}</span>
        <p>
          From your{" "}
          <span className={styles.PostAttribute}>
            <b></b>
          </span>
        </p>
      </>
    );
  }

  return <div>{postBody}</div>;
};

export default withRouter(FullPost);
