import React, { useState, useEffect } from "react";
import styles from "./Messages.module.css";
import { useSelector } from "react-redux";
import MessageSnapshot from "../../components/MessageSnapshot/MessageSnapshot";
import MessageFilter from "../MessageFilter/MessageFilter";
import Spinner from "../../components/UI/Spinners/FormSpinner/FormSpinner";

const Messages = () => {
  const [cutoff, setCutoff] = useState(14);
  const messages = useSelector((state) => state.messages.messages);
  const { who, identity } = useSelector((state) => state.messages.filters);
  const sort = useSelector((state) => state.messages.sort);
  const filteredMessages = useSelector(
    (state) => state.messages.filteredMessages
  );
  const messagesLoading = useSelector((state) => state.messages.loading);

  useEffect(() => (messages.length < 14 ? setCutoff(messages.length) : 14)),
    [messages];

  const loadMore = (count = 14) => {
    const marker =
      messages.length - cutoff >= count ? count : messages.length - cutoff;
    setCutoff(marker);
  };

  let messageFilter = messages.length > 0 ? <MessageFilter /> : null;

  let messagesContainer = messagesLoading ? <Spinner /> : null;

  if (who || identity || sort) {
    messagesContainer = filteredMessages
      .map((message, index) => {
        return (
          <MessageSnapshot
            key={index}
            entry={message}
            marker={messages.length - 1 - index}
          ></MessageSnapshot>
        );
      })
      .reverse();
  } else {
    messagesContainer = messages
      .map((message, index) => {
        return (
          <MessageSnapshot
            key={index}
            entry={message}
            marker={messages.length - 1 - index}
          ></MessageSnapshot>
        );
      })
      .reverse();
  }

  return (
    <>
      {messageFilter}
      <div className={styles.Messages}>{messagesContainer}</div>
      {messages.length - cutoff > 14 ? (
        <div onClick={() => setCutoff(14)}>LOAD MORE</div>
      ) : null}
    </>
  );
};

export default Messages;

