import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import parse from "html-react-parser";
import styles from "./FullPost.module.css";
import { setPost, resetPost } from "../../store/actions";
import Spinner from "../UI/Spinners/FormSpinner/FormSpinner";

const FullPost = props => {
  const post = useSelector(state => state.currentPost);
  const dispatch = useDispatch();

  useEffect(() => {
    const searchParams = new URLSearchParams(props.location.search);
    const postId = searchParams.get("id");

    if (!post) props.history.push("/");

    dispatch(setPost(postId));
    return () => dispatch(resetPost());
  }, [props.location.search, dispatch]);

  let postBody = <Spinner />;

  if (post) {
    postBody = (
      <>
        <p>
          To my <span className={styles.PostAttribute}>{parse(post.who)}</span>,
        </p>
        <span className={styles.PostMessage}>{parse(post.message)}</span>
        <p>
          From your{" "}
          <span className={styles.PostAttribute}>{parse(post.identity)}</span>
        </p>
      </>
    );
  }

  return <div>{postBody}</div>;
};

export default FullPost;
