import React, { Suspense } from "react";
import { Route, Switch } from "react-router-dom";
import { AnimatedSwitch } from "react-router-transition";
import styles from "./Cockpit.module.css";
import Spinner from "../../components/UI/Spinners/FormSpinner/FormSpinner";
import ContentContainer from "../ContentContainer/ContentContainer";

const Landing = React.lazy(() => import("../../components/Landing/Landing"));
const FullPost = React.lazy(() => import("../../components/FullPost/FullPost"));

const slideTransition = {
  atEnter: {
    opacity: "0%",
    right: "-100%"
  },
  atLeave: {
    opacity: 0,
    left: -100
  },
  atActive: {
    opacity: 1,
    right: 0
  }
};

const Cockpit = props => {
  let cockpitBody = (
    <Suspense fallback={<Spinner />}>
      <AnimatedSwitch
        atEnter={slideTransition.atEnter}
        atLeave={slideTransition.atLeave}
        atActive={slideTransition.atActive}
        className={styles.AnimatedWrapper}
      >
        <Route path="/post/:id">
          <ContentContainer>
            <FullPost {...props} />
          </ContentContainer>
        </Route>
      </AnimatedSwitch>
      <Route exact path="/">
        <Landing {...props} />
      </Route>
    </Suspense>
  );
  //Landing
  //Resources
  //About
  //Post

  return cockpitBody;
};

export default Cockpit;
