import React, { useState } from "react";
import styles from "./FilterOptions.module.css";
import FilterOption from "./FilterOption/FilterOption";
import SortOption from "./SortOption/SortOption";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faClock, faPen } from "@fortawesome/free-solid-svg-icons";

// import { useDispatch, useSelector } from "react-redux";
// import { addFilter, removeFilter } from "../../store/actions";
import Container from "react-bootstrap/Container";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";

const FilterOptions = props => {
  const [whoSelected, setWhoSelected] = useState(null);
  const [identitySelected, setIdentitySelected] = useState(null);
  const [sortSelected, setSortSelected] = useState(null);

  const filterHandler = (filter, name) => {
    if (whoSelected === filter && name === "who") {
      setWhoSelected(null);
    } else if (identitySelected === filter && name === "identity") {
      setIdentitySelected(null);
    } else if (name === "who") {
      setWhoSelected(filter);
    } else if (name === "identity") {
      setIdentitySelected(filter);
    }
  };

  const sortHandler = filter => {
    if (sortSelected === filter) {
      setSortSelected(false);
    } else {
      setSortSelected(filter);
    }
  };

  let classes = [styles.FilterOptions];

  if (props.expand) {
    classes = [styles.FilterOptions, styles.OpenFilterOptions].join(" ");
  }

  const whoFilters = [
    "Grandmother",
    "Grandfather",
    "Mother",
    "Father",
    "Son",
    "Daughter",
    "Grandson",
    "Granddaughter",
    "Sister",
    "Brother",
    "Cousin",
    "Friend",
    "Husband",
    "Wife",
    "Coworker",
    "Classmate",
    "Neighbor",
    "Other"
  ].map(person => (
    <FilterOption
      filterHandler={filterHandler}
      filterSelected={whoSelected}
      filter={person}
      name="who"
    />
  ));

  const identityFilters = [
    "Grandmother",
    "Grandfather",
    "Mother",
    "Father",
    "Son",
    "Daughter",
    "Grandson",
    "Granddaughter",
    "Sister",
    "Brother",
    "Cousin",
    "Friend",
    "Husband",
    "Wife",
    "Coworker",
    "Classmate",
    "Neighbor",
    "Other"
  ].map(person => (
    <FilterOption
      filterHandler={filterHandler}
      filterSelected={identitySelected}
      filter={person}
      name="identity"
    />
  ));

  const sortOptions = ["Newest", "Oldest"].map(type => (
    <SortOption
      sortHandler={sortHandler}
      sortSelected={sortSelected}
      filter={type}
    />
  ));

  return (
    <div className={styles.FilterOption}>
      <div className={classes}>
        <Container>
          <Row>
            <Col lg={4}>
              {" "}
              <span className={styles.Button}>
                <FontAwesomeIcon
                  icon={faClock}
                  size="1x"
                  style={{ color: "orange" }}
                />{" "}
                By Date
              </span>
              {whoFilters}
            </Col>
            <Col lg={4}>{identityFilters}</Col>
            <Col lg={4}>
              <span className={styles.Button}>
                <FontAwesomeIcon
                  icon={faClock}
                  size="1x"
                  style={{ color: "orange" }}
                />{" "}
                By Date
              </span>
              {sortOptions}
            </Col>
          </Row>
        </Container>
      </div>
    </div>
  );
};

export default FilterOptions;
