import React, { useState } from "react";
import MessageSnapshot from "../../components/MessageSnapshot/MessageSnapshot";
import LoadMore from "../../components/UI/Buttons/LoadMore/LoadMore";

const LoadMoreContainer = (props) => {
  const [messagesCutoff, setMessagesCutoff] = useState(14);
  const [filteredCutoff, setFilteredCutoff] = useState(14);

  let loadedMessages = null;
  let loadMoreMessages = null;

  if (props.filteredLength > 14) {
    loadMoreMessages = (count) => {
      const marker =
        props.filteredMessages.length - filteredCutoff >= count
          ? count
          : props.filteredMessages.length - filteredCutoff;
      setFilteredCutoff((prevState) => prevState + marker);
    };

    loadedMessages = props.filteredMessages
      .slice(14, filteredCutoff)
      .map((message, index) => {
        return (
          <MessageSnapshot
            key={index}
            entry={message}
            marker={index + 14}
          ></MessageSnapshot>
        );
      });
  } else {
    loadMoreMessages = (count) => {
      const marker =
        props.messages.length - messagesCutoff >= count
          ? count
          : props.messages.length - messagesCutoff;
      setMessagesCutoff((prevState) => prevState + marker);
    };

    loadedMessages = props.messages
      .slice(14, messagesCutoff)
      .map((message, index) => {
        return (
          <MessageSnapshot
            key={index}
            entry={message}
            marker={index + 14}
          ></MessageSnapshot>
        );
      });
  }

  return (
    <>
      {loadedMessages}
      <LoadMore
        loadMoreMessages={loadMoreMessages}
        filtered={props.filteredMessages.length}
      />
    </>
  );
};

export default LoadMoreContainer;
