import React, { useState, useReducer } from "react";
import styles from "./PostForm.module.css";
import Modal from "../../components/UI/Modal/Modal";
import Identity from "../../components/Identity/Identity";
import Who from "../../components/Who/Who";
import Message from "../../components/Message/Message";

const sections = [
  { section: <Identity switchHandler={switchHandler} />, complete: false },
  { section: <Who switchHandler={switchHandler} />, complete: false },
  { section: <Message switchHandler={switchHandler} />, complete: false }
];

const PostForm = () => {
  const [open, setOpen] = useState(false);
  const openHandler = () => setOpen(oldState => !oldState);
  const [sectionIndex, setSectionIndex] = useState(0);
  const switchHandler = type => {
    if (type === "next" && sectionIndex === sections.length - 1) {
      setSectionIndex(prevIndex => prevIndex + 1);
    } else if (type === "prev" && sectionIndex === 0) {
      setSectionIndex(prevIndex => prevIndex - 1);
    } else if (type === "next") {
      setSectionIndex(prevIndex => prevIndex + 1);
    } else if (type === "prev") {
      setSectionIndex(prevIndex => prevIndex - 1);
    }
  };

  return (
    <>
      <Modal open={open} openHandler={openHandler}>
        {sections[sectionIndex].section}
      </Modal>
      <div className={styles.TextBox} onClick={() => openHandler()}>
        Enter Here <span className={styles.TypeWriter}>|</span>
      </div>
    </>
  );
};

export default PostForm;
