import * as actionTypes from "./actionTypes";

export const addSort = filter => {
  return dispatch => {
    try {
      dispatch(sortPostsStart());
      dispatch(sortPostsAdd(filter));
      dispatch(sortPosts());
    } catch (err) {
      dispatch(sortPostsFail(err));
    }
  };
};

export const removeSort = filter => {
  return dispatch => {
    try {
      dispatch(sortPostsStart());
      dispatch(sortPostsRemove(filter));
      dispatch(sortPosts());
    } catch (err) {
      dispatch(sortPostsFail(err));
    }
  };
};

export const sortPostsAdd = (filter, name) => {
  return {
    type: actionTypes.SORT_POSTS_ADD,
    filter,
    name
  };
};

export const sortPostsRemove = (filter, name) => {
  return {
    type: actionTypes.SORT_POSTS_REMOVE,
    filter,
    name
  };
};
export const sortPosts = () => {
  return {
    type: actionTypes.SORT_POSTS
  };
};

export const sortPostsStart = () => {
  return {
    type: actionTypes.SORT_POSTS_START
  };
};

export const sortPostsFail = error => {
  return {
    type: actionTypes.SORT_POSTS_FAIL,
    error
  };
};

export const sortReset = () => {
  return {
    type: actionTypes.SORT_RESET
  };
};
