import React from "react";
import { useSelector } from "react-redux";

// export let interceptor;

const withErrorHandler = (WrappedComponent) => {
  return (props) => {
    // const [error, setError] = useState(false);
    // const [errorMessage, setErrorMessage] = useState("");
    const error =
      useSelector((state) => state.messages.error) ||
      useSelector((state) => state.message.error) ||
      useSelector((state) => state.persons.error);

    console.log(error);

    // interceptor = (response) => {
    //   setError(true);
    //   setErrorMessage(`${response}`);
    // };

    let body = <WrappedComponent {...props} />;

    if (error) {
      body = (
        <>
          Whoops! An error occured. IT Has been notified. Please try again
          later.
        </>
      );
    }

    return body;
  };
};

export default withErrorHandler;
