import React, { useState, useEffect } from "react";
import styles from "./FilterOption.module.css";
import { useDispatch, useSelector } from "react-redux";
import { addFilter, removeFilter } from "../../../store/actions";

// import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
// import { faFilter } from "@fortawesome/free-solid-svg-icons";

const FilterOption = props => {
  const [selected, setSelected] = useState(false);
  const filteredPosts = useSelector(state => state.filteredPosts);
  const dispatch = useDispatch();
  const selectHandler = (selected, filter, name) => {
    setSelected(prevState => !prevState);
  };
  const { filter } = props;
  console.log("filter");

  useEffect(() => {
    filterHandler(selected);
    if (!selected && filteredPosts.length > 0) {
      console.log("not selected");
      dispatch(removeFilter(filter, name));
    } else if (selected) {
      dispatch(addFilter(filter, name));
    }
    if (!selected && filteredPosts.length > 0) {
      dispatch(removeFilter(props.filter, props.name));
    } else if (selected) {
      dispatch(addFilter(props.filter, props.name));
    }
  }, [selected]);

  return (
    <div
      className={selected ? styles.FilterOptionSelected : styles.FilterOption}
      onClick={() => selectHandler(selected)}
      value="bingo"
    >
      {props.filter}
    </div>
  );
};

export default FilterOption;
