import React, { useState } from "react";

const LoadMore = (props) => {
  const [messagesCutoff, setMessagesCutoff] = useState(
    props.messages.length < 14 ? props.messages.length : 14
  );
  const [filteredCutoff, setFilteredCutoff] = useState(
    props.filteredMessages.length < 14 ? props.filteredMessages.length : 14
  );
  const loadMoreMessages = (count) => {
    const marker =
      props.messages.length - messagesCutoff >= count
        ? count
        : props.messages.length - messagesCutoff;
    setMessagesCutoff((prevState) => prevState + marker);
  };

  const loadMoreFiltered = (count) => {
    const marker =
      props.filteredMessages.length - filteredCutoff >= count
        ? count
        : props.filteredMessages.length - filteredCutoff;
    setFilteredCutoff((prevState) => prevState + marker);
  };

  const loadMessages =
    props.messages.length - messagesCutoff > 0 ? (
      <div onClick={() => loadMoreMessages(14)}>LOAD MORE</div>
    ) : null;

  const loadFiltered =
    props.filteredMessages.length - filteredCutoff > 0 ? (
      <div onClick={() => loadMoreFiltered(14)}>LOAD MORE</div>
    ) : null;

  return <></>;
};

export default LoadMore;
