import React from "react";
import styles from "./Modal.module.css";

const Modal = props => {
  let classes = [styles.Modal];

  if (props.open) {
    classes = [styles.Modal, styles.ModalOpen];
  }

  return <div className={classes.join(" ")}>{props.children}</div>;
};

export default Modal;
